function Sidebar() {
  const router = useRouter()
  const onClick = (e) => {
    router.replace(e.key)
  }
  const [menuItems, setMenuItems] = useState(items)
  const { user } = useUser()


  useEffect(() => {
    if (user?.role_id === 0) {
      const m = items.filter(item => {
        return item?.key === 'customer' || item?.key === 'transaction'
      })
      setMenuItems(m)
    }
    if (user?.role_id === 3 || user?.role_id === 4 || user?.role_id === 5) {
      const m = items.filter(item => item?.key === 'system')
      setMenuItems(m)
    }
    if (user?.role_id === 0 || user?.role_id === 3 || user?.role_id === 4 || user?.role_id === 5) {
      const m = items.filter(item => item?.key === 'transaction')
      setMenuItems(m)
    }
  }, [user])
  return (
    <div className={`${style['side-bar']}`}>
      <div className={`${style['side-bar-head']}`}>
        <p className={`${style['side-bar-title']}`}><Image src={logo} width={30} height={30} alt='logo' /> FinHub Admin</p>
      </div>
      <div className={`${style['side-bar-menu']}`} >
        <Menu
          onClick={onClick}
          style={{ width: '100%' }}
          defaultSelectedKeys={['1']}
          defaultOpenKeys={['system']}
          mode="inline"
          items={items}
        />
      </div>
    </div>
  )
}

export default Sidebar
